      if (!params.value.to) {
        showMessage('To email address is required.', 'error');
        return true;
      }

      if (!params.value.title) {
        showMessage('Subject is required.', 'error');
        return true;
      }

      let max_id = 0;

      if (!params.value.id) {
        max_id = mail_list.value.reduce((max, character) => character.id > max ? character.id : max, mail_list.value[0].id);
      }

      let c_dt = new Date();
      let obj = {
        id: max_id + 1,
        path: '',
        first_name: '',
        last_name: '',
        email: params.value.to,
        date: c_dt.getMonth() + 1 + '/' + c_dt.getDate() + '/' + c_dt.getFullYear(),
        time: c_dt.toLocaleTimeString(),
        title: params.value.title,
        display_description: quillEditorObj.value.getText(),
        type: 'draft',
        is_important: false,
        group: '',
        is_unread: false,
        description: params.value.description,
        attachments: []
      };

      if (params.value.file && params.value.file.length) {
        for (let file of params.value.file) {
          let fl_obj = {
            name: file.name,
            size: get_file_size(file.size),
            type: get_file_type(file.type)
          };
          obj.attachments.push(fl_obj);
        }
      }

      if (type == 'save' || type == 'save_reply' || type == 'save_forward') {
        //saved to draft
        obj.type = 'draft';
        mail_list.value.splice(0, 0, obj);
        tab_changed('draft');
        showMessage('Successfully Saved to Draft.');
      } else if (type == 'send' || type == 'reply' || type == 'forward') {
        //saved to sent mail
        obj.type = 'sent_mail';
        mail_list.value.splice(0, 0, obj);
        tab_changed('sent_mail');
        showMessage('Mail Sent Successfully.');
      }

      selected_mail.value = null;
      composeMailModal.value.hide();
    };

    const get_file_type = file_type => {
      let type = 'file';

      if (file_type.includes('image/')) {
        type = 'image';
      } else if (file_type.includes('application/x-zip')) {
        type = 'zip';
      }

      return type;
    };

    const get_file_size = total_bytes => {
      let size = '';

      if (total_bytes < 1000000) {
        size = Math.floor(total_bytes / 1000) + 'KB';
      } else {
        size = Math.floor(total_bytes / 1000000) + 'MB';
      }

      return size;
    };

    const showMessage = (msg = '', type = 'success') => {
      const toast = window.Swal.mixin({
        toast: true,
        position: 'top',
        showConfirmButton: false,
        timer: 3000
      });
      toast.fire({
        icon: type,
        title: msg,
        padding: '10px 20px'
      });
    };

    const __returned__ = {
      default_data,
      is_show_mail_menu,

      get mail_list() {
        return mail_list;
      },

      set mail_list(v) {
        mail_list = v;
      },

      filtered_mail_list,
      search_mail,
      selected_tab,
      selected_mail,
      params,
      mail_popup_type,
      ids,
      editor_options,
      quillEditorObj,
      composeMailModal,
      check_all_checkbox,
      initTooltip,
      initPopup,
      quillEditorReady,
      bind_mail_list,
      tab_changed,
      search_mails,
      select_mail,
      show_time,
      check_all,
      clear_selection,
      set_group,
      set_important,
      set_spam,
      set_action,
      open_mail,
      save_mail,
      get_file_type,
      get_file_size,
      showMessage,
      computed,
      onMounted,
      ref,

      get quillEditor() {
        return quillEditor;
      },

      get useMeta() {
        return useMeta;
      }

    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }

