import VNumfy from '@/components/Vumberx.vue'
import VNumber from '@/components/VNumber.vue'

const numberCounter = {
    install(Vue) {
        Vue.component(VNumfy.name, VNumfy)
        Vue.component(VNumber.name, VNumber)
    }
}

// global 情况下 自动安装
if (typeof window !== 'undefined' && window.Vue) {
    window.Vue.use(numberCounter)
}

// 导出模块
export default numberCounter;
export {VNumfy};
